import React from "react";
import PageWithForm from "./PageWithForm";
import { Link, useNavigate } from "react-router-dom";
import * as auth from '../utils/auth.js';
import InfoTooltip from "./InfoTooltip";

export default function Register({onSignUp}) {

    const [isSucceed, setIsSucceed] = React.useState(null);

    const [formValue, setFormValue] = React.useState({
        email: '',
        password: '',
      })
      const navigate = useNavigate();

      const handleChange = (e) => {
        const {name, value} = e.target;

        setFormValue({
          ...formValue,
          [name]: value
        });
      }
      const handleSubmit = (e) => {
        e.preventDefault();
          auth.register(formValue.email, formValue.password).then((res) => {
            navigate('/sign-in', {replace: true});
            setIsSucceed(true);
            }
          )
          .catch((err) => {
            console.log(err);
            setIsSucceed(false)
      })
      }

    return(
        <PageWithForm
        name = 'register'
        title = 'Регистрация'
        // buttonTitle= 'Зарегистрироваться'
        onChange={handleChange}
        onSubmit={handleSubmit}
        onClick={onSignUp}
        >
            <button type="submit" className="form-button form-button_type_white" onSubmit={onSubmit}>{ buttonTitle }</button>
            <p className="register-to-login">Уже зарегистрированы? <Link className="register-to-login_type_link" to='/sign-in'>Войти</Link></p>
            <InfoTooltip isSucceed={isSucceed}/>
        </PageWithForm>

    )
}
