import React from 'react';
import Header from './Header.js';
import Main from './Main.js';
import PopupWithForm from './PopupWithForm.js';
import Footer from './Footer.js';
import '../index.css';

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [isDeleteCardPopupOpen, setIsDeleteCardPopupOpen] = React.useState(false);

    function handleEditAvatarClick() {
        document.querySelector('.profile__avatar-overlay').addEventListener('click', setIsEditAvatarPopupOpen(true));
    };

    function handleEditProfileClick() {
        document.querySelector('.profile__edit-button').addEventListener('click', setIsEditProfilePopupOpen(true));
    };

    function handleAddPlaceClick() {
        document.querySelector('.profile__add-button').addEventListener('click', setIsAddPlacePopupOpen(true));
    };

    function handleDeleteCardClick() {
        document.querySelector('.card__remove').addEventListener('click', setIsEditAvatarPopupOpen(true));
    };

  return (
    <div className="page">
        <Header />
        <Main 
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleEditAvatarClick}
        />
        <PopupWithForm
        title = 'Редактировать профиль'
        name = 'edit'
        children = 'input'
        buttonTitle = 'Сохранить'
        isOpen = {isEditProfilePopupOpen}
        />
        <PopupWithForm
        title = 'Обновить аватар'
        name = 'avatar-edit'
        children = 'input'
        buttonTitle = 'Сохранить'
        isOpen = {isEditAvatarPopupOpen}
        />
        <PopupWithForm
        title = 'Новое место'
        name = 'add'
        children = 'input'
        buttonTitle = 'Отправить'
        isOpen = {isAddPlacePopupOpen}
        />
        <PopupWithForm
        title = 'Вы уверены?'
        name = 'delete-card'
        children = 'input'
        buttonTitle = 'Да'
        isOpen = {isEditProfilePopupOpen}
        />


        <template id="new-card">
            <li className="card">
                <img className="card__image" src="/" alt="" />
                <button className="card__remove" type="button"></button>
                <div className="card__container">
                    <h2 className="card__name"></h2>
                    <div className="card__like-container">
                        <button className="card__like" type="button"></button>
                        <h3 className="card__like-count"></h3>
                    </div>
                </div>
            </li>
        </template>

        <Footer />

    </div>
  );
}

export default App;
