import React from 'react';
import Header from './Header.js';
import Main from './Main.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.js';
import Footer from './Footer.js';
import '../index.css';
import api from '../utils/api.js';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import { CardContext } from '../contexts/CardContext.js';

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState({name:'', link:''});
    const [currentUser, setCurrentUser] = React.useState('');
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getProfileUserInfo()
        .then((userData) => {
            setCurrentUser(userData);
        })
        .catch((error) => {
            console.error(`Ошибка загрузки данных с сервера: ${error}`);
        })
    }, []);

    React.useEffect(() => {
        api.getInitialCards()
        .then((data) => {
            setCards(
                data.map((item) => ({
                    id: item._id,
                    name: item.name,
                    link: item.link,
                    likes: item.likes.length,
                }))
                )
        })
        .catch((error) => {
            console.error(`Ошибка загрузки данных с сервера: ${error}`);
          });
    }, []);



    function handleCardClick(card) {
        setSelectedCard(card);
    }

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true);
    };

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true);
    };

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true);
    };


    function closeAllPopups() {
            setIsAddPlacePopupOpen(false);
            setIsEditAvatarPopupOpen(false);
            setIsEditProfilePopupOpen(false);
            setSelectedCard({name:'', link:''});
    }

  return (
    <CurrentUserContext.Provider value={currentUser}>

        <div>
            <Header />
            <Main 
            onEditProfile={handleEditProfileClick}
            onAddPlace={handleAddPlaceClick}
            onEditAvatar={handleEditAvatarClick}
            onCardClick={handleCardClick}
            />

            <PopupWithForm
            title = 'Редактировать профиль'
            name = 'edit'
            buttonTitle = 'Сохранить'
            isOpen = {isEditProfilePopupOpen}
            onClose = {closeAllPopups}>
                <input type="text" className="form__field form__field_type_name" id="name-input" name="inputName" placeholder="Имя" minLength="2" maxLength="40" required />
                <span className='form__field-error name-input-error'></span>
                <input type="text" className="form__field form__field_type_about" id="about-input" name="inputAbout" placeholder="Обо мне" minLength="2" maxLength="200" required />
                <span className='form__field-error about-input-error'></span>
            </PopupWithForm>

            <PopupWithForm
            title = 'Обновить аватар'
            name = 'avatar-edit'
            buttonTitle = 'Сохранить'
            isOpen = {isEditAvatarPopupOpen}
            onClose = {closeAllPopups}>
                <input type="url" className="form__field form__field_type_link" id="avatar-link-input" name="inputLinkAvatar" placeholder="Ссылка" required />
                <span className='form__field-error avatar-link-input-error'></span>
            </PopupWithForm>

            <PopupWithForm
            title = 'Новое место'
            name = 'add'
            buttonTitle = 'Отправить'
            isOpen = {isAddPlacePopupOpen}
            onClose = {closeAllPopups}>
                <input type="text" className="form__field form__field_type_card-name" id="card-name-input" name="inputCardName" placeholder="Название" minLength="2" maxLength="30" required />
                <span className='form__field-error card-name-input-error'></span>
                <input type="url" className="form__field form__field_type_link" id="link-input" name="inputLink" placeholder="Ссылка" required />
                <span className='form__field-error link-input-error'></span>
            </PopupWithForm>

            <ImagePopup 
            card = { selectedCard }
            onClose = {closeAllPopups}
            />


            <Footer />

        </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
