import React from "react";
import PopupWithForm from "./PopupWithForm";
import { CardContext } from "../contexts/CardContext";

function AddPlacePopup({isOpen, onClose, onAddPlace}) {

    // const cards = React.useContext(CardContext);

    const textInputName = React.useRef('');
    const textInputLink = React.useRef('');

    function handleSubmit(e) {
        e.preventDefault();
        return onAddPlace({
            name: textInputName.current.name,
            link: textInputLink.current.link,
          });
    }
    return (
        <PopupWithForm
        title = 'Новое место'
        name = 'add'
        buttonTitle = 'Отправить'
        isOpen = {isOpen}
        onClose = {onClose}
        onSubmit={handleSubmit}>
            <input type="text" className="form__field form__field_type_card-name" id="card-name-input" name="inputCardName" placeholder="Название" minLength="2" maxLength="30" required />
            <span className='form__field-error card-name-input-error'></span>
            <input type="url" className="form__field form__field_type_link" id="link-input" name="inputLink" placeholder="Ссылка" required />
            <span className='form__field-error link-input-error'></span>
        </PopupWithForm>
    )
}

export default AddPlacePopup;
