import React from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext.js';
import api from '../utils/api.js';
import Card from './Card.js'; 

function Main({ onEditProfile, onAddPlace, onEditAvatar, onCardClick }) {

    // const [cards, setCards] = React.useState([]);

    const cards = React.useContext(Card)
    const currentUser = React.useContext(CurrentUserContext);

    React.useEffect(() => {
        api.getInitialCards()
        .then((data) => {
            setCards(
                data.map((item) => ({
                    id: item._id,
                    name: item.name,
                    link: item.link,
                    likes: item.likes.length,
                }))
                )
        })
        .catch((error) => {
            console.error(`Ошибка загрузки данных с сервера: ${error}`);
          });
    }, []);


    return(
        <main className="main">
            <section className="profile">
                <div className="profile__container">
                    <img className="profile__avatar" src={ currentUser.avatar } alt="фото автора" />
                    <div className="profile__avatar-overlay" onClick={onEditAvatar}></div>
                </div>
                <div className="profile__info">
                    <h1 className="profile__field-name">{ currentUser.name }</h1>
                    <button className="profile__edit-button" type="button" onClick={onEditProfile}></button>
                    <p className="profile__field-about">{ currentUser.about }</p>
                </div>
                <button className="profile__add-button" type="button" onClick={onAddPlace}></button>
            </section>

            <section className="gallery">
                <ul className="gallery__list">
                    {cards.map((item) => (
                        <Card key={ item.id } card={ item } onCardClick = {onCardClick}/>
                    ))}
                </ul>
            </section>
        </main>
    );


}

export default Main;
