import React from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import PopupWithForm from "./PopupWithForm";

function EditProfilePopup({isOpen, onClose, onUpdateUser}) {

    const currentUser = React.useContext(CurrentUserContext);

    React.useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
      }, [currentUser]); 

    const [name, setName] = React.useState('');
    const [description, setDescription] = React.useState('');

    function handleChange(e) {
        setName(e.target.name);
        setDescription(e.target.description);
    }

    function handleSubmit(e) {
        e.preventDefault();
        onUpdateUser({
            name: name,
            about: description,
          });
    }


    return (
        <PopupWithForm
        title = 'Редактировать профиль'
        name = 'edit'
        buttonTitle = 'Сохранить'
        onSubmit={handleSubmit}
        isOpen={isOpen}
        onClose={onClose}>
            <input type="text" className="form__field form__field_type_name" id="name-input"  placeholder="Имя" minLength="2" maxLength="40" required value={name} onChange={handleChange}/>
            <span className='form__field-error name-input-error'></span>
            <input type="text" className="form__field form__field_type_about" id="about-input" name="inputAbout" placeholder="Обо мне" minLength="2" maxLength="200" required value={description} onChange={handleChange}/>
            <span className='form__field-error about-input-error'></span>
        </PopupWithForm>
    )
}

export default EditProfilePopup;
