                <div className="profile__avatar-overlay"></div>
            </div>
            <div className="profile__info">
                <h1 className="profile__field-name">Helga Ish</h1>
                <button className="profile__edit-button" type="button"></button>
                <p className="profile__field-about">Linguist, frontender</p>
            </div>
            <button className="profile__add-button" type="button"></button>
        </section>

        <section className="gallery">
            <ul className="gallery__list">

            </ul>

        </section>
    </main>
    );
    function handleEditAvatarClick() {
        const popupAvatar = document.querySelector('#popup-avatar-edit');
        const popupEditAvatarButton = document.querySelector('.profile__avatar-overlay');
        popupEditAvatarButton.addEventListener('click', () => {
            popupEdit.classList.add('popup_active');
        })
    };
    function handleEditProfileClick() {
        const popupEdit = document.querySelector('#popup-edit');
        const popupEditButton = document.querySelector('.profile__edit-button');
        popupEditButton.addEventListener('click', () => {
            popupEdit.classList.add('popup_active');
        })
    };

    function handleAddPlaceClick() {
        const popupAddPlace = document.querySelector('#popup-add');
        const popupAddPlaceButton = document.querySelector('.profile__add-button');
        popupEditButton.addEventListener('click', () => {
            popupEdit.classList.add('popup_active');
        })
    };
}

export default Main;
