import logo from '../images/logo.svg';
import { Link, useNavigate } from 'react-router-dom';
import React from 'react';
import * as auth from '../utils/auth.js';

function Header({loggedIn, handleLogin}) {

    const [email, setEmail] = React.useState('');
    const navigate = useNavigate();

    React.useEffect (() => {
        getEmail();
        console.log('');
    })

    function getEmail() {
        if (localStorage.getItem('jwt')) {
            const jwt = localStorage.getItem('jwt');
            auth.getEmail(jwt).then((data) => {
                if(data) {
                    setEmail(data.email);
                    handleLogin();
                    navigate('/', {replace: true})
                }
            });
            console.log('meow');
        }
    }


    return(
        <header className="header">
            <img className="logo" src={logo} alt="логотип mesto" />
            {/* <Link className='header__link' to="/sign-up">Зарегистрироваться</Link> */}
            {loggedIn ? (
                <Link className='header__link' to="/">Выйти</Link>
            ) : (
                <div>
                    <p className='header__email'>{ email }</p>
                    <Link className='header__link' to="/sign-in">Войти</Link>
                </div>
            )
            }
        </header>
    )
}

export default Header;
