function ImagePopup({ card, popupName, onClose}) {
    const className = `popup ${isOpen ? 'popup_active' : ''}`;
    return(
        <section className={ className } id={`popup-${popupName}`} >
        <div className="popup-card">
            <img className="popup-card__image" src={card.link} alt={ card.name } />
            <h2 className="popup-card__heading">{ card.name }</h2>
            <button className="popup__toggle" type="button"></button>
        </div>
    </section>
    );
}

export default ImagePopup;
