function PopupWithForm({ title, name, children, buttonTitle, isOpen, onClose }) {

    const className = `popup ${isOpen ? 'popup_active' : ''}`;


    return(
        <section className= {className} id={`popup-${name}`} isOpen={false}>
        <div className="popup__container">
            <button className="popup__toggle" type="button" onClick={onClose}></button>
            <form className="form" name={`${name}Form`} noValidate>
                <h2 className="form__heading">{`${title}`}</h2>
                <fieldset className="form__fields">
                    {children}
                    <input type="text" className="form__field" required />
                    <span className='form__field-error'></span>
                    <input type="text" className="form__field" required />
                    <span className='form__field-error'></span>
                </fieldset>
                <button type="submit" className="form-button">{ buttonTitle }</button>
            </form>
        </div>
    </section>
    )
}

export default PopupWithForm;
