import React from "react";

export default function InfoTooltip({isSucceed ,onClose}) {
    return(
        <section className="signup-confirmation" id="popup-signup">
            <div className="popup__container">
            <button className="popup__toggle" type="button" onClick={onClose}></button>

                <img className="popup__success-signup" src="/" alt="/"/>
                <p className="popup__success-signup-message">Вы успешно зарегистрировались!</p>
                <img className="popup__fail-signup" src="/" alt="/"/>
                <p className="popup__fail-signup-message">Что-то пошло не так! Попробуйте ещё раз.</p>
            </div>
        </section>
    )
}
