import React from "react";
import PageWithForm from "./PageWithForm";
import { Link, useNavigate } from "react-router-dom";
import * as auth from '../utils/auth.js';
import InfoTooltip from "./InfoTooltip";
import success from '../images/sign-up-success.svg';
import fail from '../images/sign-up-fail.svg'

export default function Register() {

    const [isInfoToolTipPopupOpen, setIsInfoToolTipPopupOpen] = React.useState(false);

    function handleInfoToolTipClick() {
        setIsInfoToolTipPopupOpen(true);
    };
    function closeInfoToolTipPopup() {
        setIsInfoToolTipPopupOpen(false);
    }

    const [isSucceed, setIsSucceed] = React.useState(null);

    const [formValue, setFormValue] = React.useState({
        email: '',
        password: '',
      })
      const navigate = useNavigate();

      const handleChange = (e) => {
        const {name, value} = e.target;

        setFormValue({
          ...formValue,
          [name]: value
        });
      }
      const handleSubmit = (e) => {
        e.preventDefault();
        handleInfoToolTipClick();
          auth.register(formValue.email, formValue.password).then((res) => {
                // navigate('/sign-in', {replace: true});
                console.log(res.ok);
                setIsSucceed(true);
            })
            .catch((error) => {
              setIsSucceed(false)
            });
      }

    return(
        <PageWithForm
        name = 'register'
        title = 'Регистрация'
        buttonTitle= 'Зарегистрироваться'
        onChange={handleChange}
        onSubmit={handleSubmit}
        formValueEmail={formValue.email}
        formValuePassword={formValue.password}
        >
            <p className="register-to-login">Уже зарегистрированы? <Link className="register-to-login_type_link" to='/sign-in'>Войти</Link></p>
            <InfoTooltip
            // isSucceed={isSucceed}
            isOpen={isInfoToolTipPopupOpen}
            onClose={closeInfoToolTipPopup}>
                              {isSucceed ? (
                    <div>
                        <img className="popup__signup-sign" src={success} alt="успешная регистрация"/>
                        <p className="popup__signup-message">Вы успешно зарегистрировались!</p>
                    </div>
                ) : (
                    <div>
                        <img className="popup__signup-sign" src={fail} alt="неуспешная регистрация"/>
                        <p className="popup__signup-message">Что-то пошло не так! Попробуйте ещё раз.</p>
                    </div>
                )}
            </InfoTooltip>
        </PageWithForm>

    )
}
